import 'package:cloud_firestore/cloud_firestore.dart';
import 'package:get/get.dart';
import 'package:music_player_app/model/my_song_model.dart';

class CloudSongController extends GetxController {
  final db = FirebaseFirestore.instance;

  RxList<MySongModel> cloudSongList = RxList<MySongModel>([]);
  RxList<MySongModel> trendingSongList = RxList<MySongModel>([]);

  @override
  void onInit() {
    getCloudSound();
    // TODO: implement onInit
    super.onInit();
  }

  void uploadSongToDatabase() async {
    MySongModel newSong = MySongModel(
        id: 1,
        title: "Main Ladega Anthem (320 Kbps)",
        artist: "arijit singh",
        album: "album",
        albumArt:
            "data:image/jpeg;base64,",
        data:
            "https://firebasestorage.googleapis.com/v0/b/audio-player-cfbab.appspot.com/o/01%20-%20Main%20Ladega%20Anthem%20(320%20Kbps).mp3?alt=media&token=c072a454-cb47-425c-bee4-d48c84651b2e");
    await db.collection("songs").add(newSong.toJson());
    print("song uploaded");
  }

  void getCloudSound() async {
    cloudSongList.clear();
    await db.collection("songs").get().then(
          (value) => {
            value.docs.forEach(
              (element) {
                cloudSongList.add(
                  MySongModel.fromJson(
                    element.data(),
                  ),
                );
              },
            )
          },
        );
    await db.collection("tranding").get().then(
          (value) => {
        value.docs.forEach(
              (element) {
            trendingSongList.add(
              MySongModel.fromJson(
                element.data(),
              ),
            );
          },
        )
      },
    );
  }
}
